import React, { useState, useEffect } from 'react';
import '../index.css';
import Footer from './Footer';
import Header from './Header';
import Main from './Main';
import ImagePopup from './ImagePopup';
import api from '../utils/api';
import { CurrentUserContext, currentUserInfo } from '../contexts/CurrentUserContext';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import ConfirmDeletePopup from './ConfirmDeletePopup';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import ProtectedRoute from './ProtectedRoute';
import * as auth from '../utils/auth';
import InfoTooltip from './InfoTooltip';
import Login from './Login';
import Register from './Register';
import UnionBlack from '../images/UnionBlack.svg'
import UnionRed from '../images/UnionRed.svg'


function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  const [isImagePopupOpen, setIsImagePopupOpen] = useState(false);

  const [isConfirmDeletePopupOpen, setIsConfirmDeletePopupOpen] = useState(false);
  const [isInfoTooltipPopupOpen, setInfoTooltipPopupOpen] = useState(false);

  const [renderLoadingOn, setIsRenderLoadingOn] = useState(false);

  const [selectedCard, setSelectedCard] = useState(null);
  const [cards, setCards] = useState([]);
  const [currentUser, setCurrentUser] = useState(currentUserInfo);

  const [email, setEmail] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [popupStatus, setPopupStatus] = useState({ image: '', message: '' });

  const [isSuccess, setIsSuccess] = useState(false);

  const navigate = useNavigate();


  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      auth.checkToken(userId)
        .then((res) => {
          if (res) {
            setIsLoggedIn(true);
            setEmail(res.email);
            navigate('/', { replace: true });
          }
        })
        .catch((err) => {
          localStorage.removeItem('userId');
          console.log(err);
        });
    }
  },
    [navigate]);

  // Регистрация пользователя
  const handleRegisterSubmit = (email, password) => {
    auth.register(email, password)
      .then((res) => {
        if (res) {
          setPopupStatus({
            image: UnionBlack,
            message: 'Вы успешно зарегистрировались!'
          });
          navigate("/sign-in");
          setIsLoggedIn(true);
          setEmail(res.email);
        }
      })
      .catch(() => {
        setPopupStatus({
          image: UnionRed,
          message: 'Что-то пошло не так! Попробуйте еще раз.'
        });
      })
      .finally(handleInfoTooltip);
  };


  // Вход в аккаунт
  const handleLoginSubmit = (email, password) => {
    auth.authorize(email, password)
      .then((res) => {
        localStorage.setItem('userId', res._id);
        setIsLoggedIn(true);
        setEmail(res.email);
        navigate("/")

      })
      .catch((err) => {
        setPopupStatus({ image: UnionRed, message: 'Что-то пошло не так! Попробуйте еще раз.' });
        handleInfoTooltip();
      });
  };


  const handleSignOut = () => {
    localStorage.removeItem('userId');
    setIsLoggedIn(false);
    setEmail(null);
    setCards([]);
    navigate('/', { replace: true });
  };

  // Получение данных текущего пользователя и начальных карточек
  useEffect(() => {
    if (isLoggedIn) {
      api.getMyInfo()
        .then((data) => {
          setCurrentUser(data)
        })
        .catch((err) => { console.log(err) })
    }
  }, [isLoggedIn]);

  useEffect(() => {
    if (isLoggedIn) {
      api.getServerCards()
        .then((cards) => {
          setCards(cards);
        })
        .catch((err) => { console.log(err) })
    }
  }, [isLoggedIn])



  function handleInfoTooltip() {
    setInfoTooltipPopupOpen(true);
  };

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((like) => like._id === currentUser._id);
    // Отправляем запрос в API и получаем обновлённые данные карточки
    if (!isLiked) {
      api.addLike(card._id, isLiked).then((newCard) => {
        setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
      }).catch((err) => {
        console.error(err);
      });
    } else {
      api.deleteLike(card._id).then((newCard) => {
        setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
      }).catch((err) => {
        console.error(err);
      });
    }
  }

  function handleCardDelete(card) {
    api.deleteCard(card._id).then(() => {
      setCards((state) => state.filter((c) => c._id !== card._id && c));
      closeAllPopups()
    }).catch((err) => {
      console.error(err);
    });
  }

  function handleChangeProfile(userData) {
    api
      .changeProfileData(userData)
      .then((serverUserData) => {
        setCurrentUser(serverUserData);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleChangeAvatar(userAvatar) {
    api.changeUserAvatar(userAvatar).then((serverUserAvatar) => {
      setCurrentUser(serverUserAvatar);
      closeAllPopups();
    }).catch((err) => {
      console.error(err);
    })
  }

  function handleAddPlaceSubmit(items) {
    api
      .addCard(items)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function closePopupEscape(event) {
    if (event.key === 'Escape') {
      closeAllPopups();
    }
  }

  function closePopupOverlay(event) {
    if (event.target.classList.contains('popup_opened')) {
      closeAllPopups();
    }
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
    setIsConfirmDeletePopupOpen(false);
    setInfoTooltipPopupOpen(false);
  }




  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Routes>
          <Route path='/sign-up'
            element={
              <>
                <Header
                  title='Войти'
                  route='/sign-in'
                />
                <Register
                  onRegister={handleRegisterSubmit}
                />
              </>
            }
          />
          <Route path='/sign-in'
            element={
              <>
                <Header
                  title='Регистрация'
                  route='/sign-up'
                />
                <Login
                  onLogin={handleLoginSubmit}
                />
              </>
            }
          />
          <Route path='/'
            element={
              <>
                <Header
                  title='Выйти'
                  route=''
                  email={email}
                  onClick={handleSignOut}
                />
                <ProtectedRoute
                  path="/"
                  component={Main}
                  isLoggedIn={isLoggedIn}
                  onEditAvatar={() => setIsEditAvatarPopupOpen(true)}
                  onEditProfile={() => setIsEditProfilePopupOpen(true)}
                  onAddPlace={() => setIsAddPlacePopupOpen(true)}
                  onCardClick={(card) => setSelectedCard(card)}
                  cards={cards}
                  onCardLike={handleCardLike}
                  onConfirmCardDelete={(card) => setIsConfirmDeletePopupOpen(card)}
                />
                <Footer />
              </>
            }
          />
          <Route
            path="*"
            element={
              isLoggedIn ? <Navigate to="/" /> : <Navigate to="/sign-in" />
            }
          />
        </Routes>

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onCloseEscape={closePopupEscape}
          onCloseOverlay={closePopupOverlay}
          onChangeUser={handleChangeProfile}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onCloseEscape={closePopupEscape}
          onCloseOverlay={closePopupOverlay}
          onChangeAvatar={handleChangeAvatar}
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onCloseEscape={closePopupEscape}
          onCloseOverlay={closePopupOverlay}
          onAddPlace={handleAddPlaceSubmit}
        />
        <ConfirmDeletePopup
          card={isConfirmDeletePopupOpen}
          name="popup_card-delete"
          title="Вы уверены?"
          textButton="Да"
          onClose={closeAllPopups}
          onCardDelete={handleCardDelete}
        />
        <ImagePopup
          card={selectedCard}
          onClose={closeAllPopups}
          onCloseEscape={closePopupEscape}
          onCloseOverlay={closePopupOverlay}
        />
        <InfoTooltip
          popupStatus={popupStatus}
          isOpen={isInfoTooltipPopupOpen}
          onClose={closeAllPopups}
          onCloseEscape={closePopupEscape}
          onCloseOverlay={closePopupOverlay}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
