import React, { useState, useEffect } from 'react';
import '../index.css';
import Footer from './Footer';
import Header from './Header';
import Main from './Main';
import ImagePopup from './ImagePopup';
import api from '../utils/api';
import { CurrentUserContext, currentUserInfo } from '../contexts/CurrentUserContext';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import ConfirmDeletePopup from './ConfirmDeletePopup';
import { Routes, Route, useNavigate } from 'react-router-dom';
import ProtectedRoute from './ProtectedRoute';
import * as auth from '../utils/auth';
import InfoToolTip from './InfoTooltip';
import Login from './Login';
import Register from './Register';

function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  const [isImagePopupOpen, setIsImagePopupOpen] = useState(false);

  const [isConfirmDeletePopupOpen, setIsConfirmDeletePopupOpen] = useState(false);
  const [isInfoToolTipPopupOpen, setInfoToolTipPopupOpen] = useState(false);

  const [renderLoadingOn, setIsRenderLoadingOn] = useState(false);

  const [selectedCard, setSelectedCard] = useState(null);
  const [cards, setCards] = useState([]);
  const [currentUser, setCurrentUser] = useState(currentUserInfo);

  const [email, setEmail] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // const [popupStatus, setPopupStatus] = useState({ image: '', message: '' });

  const [isSuccess, setIsSuccess] = useState(false);

  const navigate = useNavigate();


  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      auth.checkToken(userId)
        .then((res) => {
          if (res) {
            setIsLoggedIn(true);
            setEmail(res.email);
            navigate('/', { replace: true });
          }
        })
        .catch((err) => {
          if (err.status === 400) {
            console.log('400 — Токен не передан или передан не в том формате');
          } else if (err.status === 401) {
            console.log('401 — Переданный токен некорректен');
          }
        });
    }
  }, []);

  // [navigate]);

  // Регистрация пользователя
  const handleRegisterSubmit = (email, password) => {
    auth.register(email, password)
      .then((res) => {
        // if (res) {
        //         setPopupStatus({
        //           image: UnionBlack,
        //           message: 'Вы успешно зарегистрировались!'
        //         });
        //         navigate("/sign-in");
        //         setIsLoggedIn(true);
        //         setEmail(res.email);
        //       }
        //     })
        //     .catch(() => {
        //       setPopupStatus({
        //         image: UnionRed,
        //         message: 'Что-то пошло не так! Попробуйте еще раз.'
        //       });
        //     })
        //     .finally(handleInfoTooltip);
        // };
        setInfoToolTipPopupOpen(true);
        setIsSuccess(true);
        navigate('/sign-in');
      })
      .catch(err => {
        if (err.status === 400) {
          console.log('400 - некорректно заполнено одно из полей');
        }
        setInfoToolTipPopupOpen(true);
        setIsSuccess(false);
      });
  }



  // Вход в аккаунт
  const handleLoginSubmit = (email, password) => {
    auth.login(email, password)
      .then((res) => {
        localStorage.setItem('userId', res._id);
        setIsLoggedIn(true);
        setEmail(res.email);
        navigate("/")

      })
      .catch((err) => {
        // setPopupStatus({ image: UnionRed, message: 'Что-то пошло не так! Попробуйте еще раз.' });
        // handleInfoTooltip();
        //     });
        // };
        if (err.status === 400) {
          console.log('400 - не передано одно из полей');
        } else if (err.status === 401) {
          console.log('401 - пользователь с email не найден');
        }
        setInfoToolTipPopupOpen(true);
        setIsSuccess(false);
      });
  }

  const handleSignOut = () => {
    localStorage.removeItem('userId');
    setIsLoggedIn(false);
    setEmail(null);
    setCards([]);
    navigate('/', { replace: true });
  };

  // Получение данных текущего пользователя и начальных карточек
  useEffect(() => {
    if (isLoggedIn) {
      api.getMyInfo()
        .then((data) => {
          setCurrentUser(data)
        })
        .catch((err) => { console.log(err) })
    }
  }, [isLoggedIn]);

  useEffect(() => {
    if (isLoggedIn) {
      api.getServerCards()
        .then((cards) => {
          setCards(cards);
        })
        .catch((err) => { console.log(err) })
    }
  }, [isLoggedIn])



  // function handleInfoTooltip() {
  //   setInfoToolTipPopupOpen(true);
  // };

  function renderLoading() {
    setIsRenderLoadingOn((renderLoadingOn) => !renderLoadingOn);
  }


  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }


  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImagePopupOpen(true)
  }

  function handleConfirmDeleteClick(card) {
    setSelectedCard(card);
    setIsConfirmDeletePopupOpen(true)
  }



  // function handleCardLike(card) {
  //   // Снова проверяем, есть ли уже лайк на этой карточке
  //   const isLiked = card.likes.some((like) => like._id === currentUser._id);
  //   // Отправляем запрос в API и получаем обновлённые данные карточки
  //   if (!isLiked) {
  //     api.addLike(card._id, isLiked).then((newCard) => {
  //       setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
  //     }).catch((err) => {
  //       console.error(err);
  //     });
  //   } else {
  //     api.deleteLike(card._id).then((newCard) => {
  //       setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
  //     }).catch((err) => {
  //       console.error(err);
  //     });
  //   }
  // }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((err) => { console.log(err) })
  }



  // function handleCardDelete(card) {
  //   api.deleteCard(card._id).then(() => {
  //     setCards((state) => state.filter((c) => c._id !== card._id && c));
  //     closeAllPopups()
  //   }).catch((err) => {
  //     console.error(err);
  //   });
  // }

  function handleCardDelete() {
    api.deleteCard(selectedCard._id)
      .then(() => {
        setCards((cards) => cards.filter((item) => item._id !== selectedCard._id))
        closeAllPopups()
      })
      .catch((err) => { console.log(err) })
      .finally(() => renderLoading())
  }

  function handleAddCard(card) {
    api.addNewCard(card)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => { console.log(err) })
      .finally(() => renderLoading())
  }



  function handleChangeProfile(userData) {
    api
      .changeProfileData(userData)
      .then((serverUserData) => {
        setCurrentUser(serverUserData);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
      .finally(() => renderLoading())
  }

  function handleChangeAvatar(userAvatar) {
    api.changeUserAvatar(userAvatar).then((serverUserAvatar) => {
      setCurrentUser(serverUserAvatar);
      closeAllPopups();
    }).catch((err) => { console.log(err) })
      .finally(() => renderLoading())
  }


  // function handleAddPlaceSubmit(items) {
  //   api
  //     .addCard(items)
  //     .then((newCard) => {
  //       setCards([newCard, ...cards]);
  //       closeAllPopups();
  //     })
  //     .catch((err) => console.log(err));
  // }



  function closePopupEscape(event) {
    if (event.key === 'Escape') {
      closeAllPopups();
    }
  }

  function closePopupOverlay(event) {
    if (event.target.classList.contains('popup_opened')) {
      closeAllPopups();
    }
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
    setIsConfirmDeletePopupOpen(false);
    setInfoToolTipPopupOpen(false);
  }




  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header
          email={email}
          onSignOut={handleSignOut}
          isLoggedIn={isLoggedIn}
        />
        <Routes>
          <Route path='/'
            element={<ProtectedRoute
              onEditAvatar={handleEditAvatarClick}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onCardClick={handleCardClick}
              onCardLike={handleCardLike}
              onCardDelete={handleConfirmDeleteClick}
              cards={cards}
              loggedIn={isLoggedIn}
              element={Main}
            />}
          />
          <Route path="/sign-in"
            element={<Login onLogin={handleLoginSubmit} />} />
          <Route path="/sign-up"
            element={<Register onRegister={handleRegisterSubmit} />} />
        </Routes>
        {isLoggedIn && <Footer />}
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateProfile={handleChangeProfile}
          renderLoadingOn={renderLoadingOn}
          renderLoading={renderLoading}
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddCard}
          renderLoadingOn={renderLoadingOn}
          renderLoading={renderLoading}
        />
        <ImagePopup
          card={selectedCard}
          isOpen={isImagePopupOpen}
          onClose={closeAllPopups}
        />
        <ConfirmDeletePopup
          isOpen={isConfirmDeletePopupOpen}
          onClose={closeAllPopups}
          onDeleteCard={handleCardDelete}
          renderLoadingOn={renderLoadingOn}
          renderLoading={renderLoading}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onChangeAvatar={handleChangeAvatar}
          renderLoadingOn={renderLoadingOn}
          renderLoading={renderLoading}
        />
        <InfoToolTip
          isOpen={isInfoToolTipPopupOpen}
          onClose={closeAllPopups}
          isSuccess={isSuccess}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
